import React from 'react'
import { TrendData } from '../Data/TrendData'
import './TrendCard.css'

const TrendCard = () => {
  return (
    <div className="TrendCard">
        <h3>Trends for you</h3>
        {TrendData.map((trend)=>{
            return(
                <div className="trend">
                    <span>#{trend.name}</span>
                    <span>#{trend.shares}K Shares</span>

                </div>
            )
        })}
    </div>
  )
}

export default TrendCard
