import React from 'react'
import './comment.css'
import { useState } from 'react'

const Comment = () => {
    const [show, setshow] = useState(false)
    return (
        <div >

      
        <div className='comment'>
            <input className='writeComment' type="text" placeholder='Write Your Comments Here' style={{ color: "var(--phone)" }}

            ></input>
            <button className='commentButton' onClick={()=>setshow((show)=>!show) }>post</button>
        </div>
        <div>
            <p>UserName</p>
            <p>Comment</p>
        </div>
        
        </div>

    

    )
}

export default Comment
