import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react'
import './Auth.css'

const Auth = () => {

  const [IsSignUp, setSignUp] = useState(false);
  const [values, setValues] = useState({
    firstname: "",
    lastname: "",
    username: "",
    password: "",
    confirmpass: "",
  });
  const [confirmPass, setConfirmPass] = useState(true);

//The event gets passed in by React, and we want to update our state object. So to do that,
// we call the setValues function and pass in a new object with the updated values.
  const handleChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value})
  }

  return (
    <div className="Auth" >
      <div className="a-right">

        <form className='infoForm authForm'>


          <h3>{IsSignUp ? "SignUp" : "Login"}</h3>

          {IsSignUp &&

            <div className='inputname'>
              <input type="text" placeholder='First Name' className='infoInput' name='firstname'  value={values.firstname} onChange={handleChange} />
              <input type="text" placeholder='Last Name' className='infoInput' name='lastname' onChange={handleChange} />

            </div>

          }
          <div className='infoInput1' >
            <div>
              <input type="text" className="infoInput" name='username' placeholder='UserName' style={{ width: "90%" }} onChange={handleChange} />
            </div>
            <div className='infoInput2'>
              <input type="password" className='infoInput' name='password' placeholder='Password' onChange={handleChange} />
              {IsSignUp &&
                
                  <input type="password"  className='infoInput' name='confirmpass' placeholder='Confirm Password' onChange={handleChange}/>

                
              }
            </div>
          </div>
          <div>
            <span style={{ fontSize: "12px" }} onClick ={()=>setSignUp((prev)=>(!prev))}>
             {IsSignUp  ?  "Already Have an Account! Login":"Don't Have an account! SignUp"} 
            </span>
          </div>
          
          <button className='button infoButton' type='submit' >{IsSignUp?"Sign Up":"Login"}</button>

        </form>
      </div>
    </div>
  )
}

export default Auth
