import React from 'react'
import './comment.css'
import share from'../../img/share.png'

const Comment = () => {
    return (
        <div className='comment'>
            <input className='writeComment' type="text" placeholder='Write Your Comments Here' style={{ color: "var(--phone)" }}

            ></input>
            <button className='commentButton' >{share}</button>
        </div>


    )
}

export default Comment
