import React from 'react'
import './../Post/Post.css'
import {likePost} from '.././Api/PostRequest'

import Comment from './../img/comment.png'
import Share  from './../img/share.png'
import Like  from './../img/like.png'
import NotLike from './../img/notlike.png'
import { useSelector } from 'react-redux'
import { useState } from 'react'




const Post = ({data}) => {


    const {user}= useSelector((state)=>state.authReducer.authData)
    const [liked, setLiked] =useState(data.likes.includes(user._id))
    const [likes,setLikes] =useState(data.likes.length)
    const[show, setshow] =useState(false);


    
    const handleLike =()=>{
      
     likePost(data._id,user._id);
      setLiked((prev) => !prev);
      liked? setLikes((prev)=>prev-1): setLikes((prev)=>prev+1)
    }
  return (
    <div className="Post">
      <img
        src={data.Image ? process.env.REACT_APP_PUBLIC_FOLDER + data.Image : ""}
        alt=""
      />
    <div className="icon">
            <div className="postReact">
                <img src={liked?Like:NotLike} alt ="" style={{cursor:'pointer'}} onClick={handleLike}/>
                <img src={Comment}alt="" className='comment' onClick={()=>setshow((show)=> !show)}/>
                <img src={Share} alt="" />
                
            

        
              
                
            </div>
            </div>
            <span  style={{color:"var(--gray)",fontSize:'12px'}}>{likes} likes</span>
        
        <div className="divdetail">
            <span><b>{data.name}</b></span>
            <span> {data.desc}</span>

            {show?<><div className='comment'>
            <input className='writeComment' type="text)" placeholder='Write Your Comments Here' style={{ color: "var(--phone)" }}

            ></input>
            <button className='commentButton' type='submit'>post</button>
        </div>
        <div>
            <p>UserName</p>
            <p>Comment</p>
        </div></>:null}

        </div>
    </div>
  )
}

export default Post
